import React, { useState } from "react";
import axios from "axios";

function LoanStatus() {
  const [loans, setLoans] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8000/loan/viewLoanStatus");
      const data = response.data;
      if (response.status === 200) {
        setLoans(data.result); // Access the nested result array
      } else {
        alert(data.error || 'Failed to fetch loan status');
      }
    } catch (error) {
      console.error("Error fetching loan status:", error);
      alert(`Failed to fetch loan status: ${error.message}`);
    }
  };

  return (
    <div>
      <button onClick={handleSubmit}>View Loans</button>
      {loans.length > 0 ? (
        <div>
          {loans.map((loan, index) => (
            <div key={index}>
              <p>Loan No: {loan.loan_no}</p>
              <p>Bank Response Date: {loan.bank_response_date}</p>
              <p>Application Date: {loan.application_date}</p>
              <p>Loan Amount: {loan.loan_amount}</p>
              <p>Remark: {loan.remark}</p>
              <p>Approval Status: {loan.approval_status}</p>
              <hr />
            </div>
          ))}
        </div>
      ) : (
        <p></p>
      )}
    </div>
  );
}

export default LoanStatus;
