import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const AmountOwed = () => {
    const { username } = useParams();
    const [password, setPassword] = useState('');
    const [owedAmount, setOwedAmount] = useState(null);

    useEffect(() => {
        if (username) {
            // Fetch owed amount based on username
            fetchOwedAmount(username);
        }
    }, [username]);

    const fetchOwedAmount = async (username) => {
        // Fetch owed amount using username
        const response = await fetch(`/api/owedAmount?username=${username}`);
        const data = await response.json();
        if (response.ok) {
            setOwedAmount(data.owed_amount);
        } else {
            setOwedAmount(null);
            alert('Error fetching owed amount');
        }
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleLogin = async () => {
        // Perform login action here
        // You can use the username and password to authenticate the user
        // Once authenticated, you may fetch the owed amount again if needed
    };

    return (
        <div>
            <p>Username: {username}</p>
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={handlePasswordChange}
            />
            <button onClick={handleLogin}>Login</button>
            {owedAmount !== null ? (
                <p>Owed Amount: {owedAmount}</p>
            ) : (
                <p>Loading owed amount...</p>
            )}
        </div>
    );
};

export default AmountOwed;
