import React, {useState} from 'react'
import axios from "axios";

function ApproveLoans() {
    
const [data,setData] = useState(null);
const handleView = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get("http://localhost:8000/admin/pendingLoans");
      setData(response.data);
      if (response.status === 200) {
        console.log(data)
      } else {
        alert(data.error || "Failed to deposit money");
      }
    } catch (error) {
      console.error("Error depositing money:", error);
      alert(`Failed to deposit money: ${error.message}`);
    }
  };

  return (
    <>
    
    <button onClick={handleView}> view Pending Loans</button>
    {data && (
        <div>
          <h3>Pending Loans:</h3>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Amount</th>
                <th>Borrower</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {data.map((loan) => (
                <tr key={loan.id}>
                  <td>{loan.id}</td>
                  <td>{loan.amount}</td>
                  <td>{loan.borrower}</td>
                  <td>
                    <button onClick={() => handleApprove(loan.id)}>Approve</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    {/* <form onSubmit={handleSubmit}>
        <input
          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Set Interest Rate"
        />
        <button type="submit">Set Interest Rate</button>
      </form> */}
      </>
  )
}

export default ApproveLoans
