import React from 'react'

let data;
const handleView = async (e) => {
    e.preventdefault();
    try {
      const response = await axios.get("http://localhost:8000/admin/pendingLoans");
      data = response.data;
      if (response.status === 200) {
        alert("Deposit successful");
      } else {
        alert(data.error || "Failed to deposit money");
      }
    } catch (error) {
      console.error("Error depositing money:", error);
      alert(`Failed to deposit money: ${error.message}`);
    }
  };

function ApproveLoans() {
  return (
    <>
    
    <button onClick={handleView}> vie Pending Loans</button>
    
    </>
  )
}

export default ApproveLoans
