          type="number"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="Set Interest Rate"
        />
        <button type="submit">Set Interest Rate</button>
      </form>
    {data && (
        <div>
          <h3>Pending Loans:</h3>
          <pre>{JSON.stringify(data, null, 2)}</pre>
        </div>
      )}    </>
  )
}

export default ApproveLoans
