import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const AmountOwed = () => {
    const { username } = useParams();
    const [password, setPassword] = useState('');
    const [owedAmount, setOwedAmount] = useState(null);

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleLogin = async () => {
        // Perform login action
        const response = await fetch('/api/loanAmount', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username: username,
                password: password
            })
        });
        const data = await response.json();
        if (response.ok) {
            setOwedAmount(data.loan_amount);
        } else {
            setOwedAmount(null);
            alert(data.error || 'Error fetching owed amount');
        }
    };

    return (
        <div>
            <p>Username: {username}</p>
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={handlePasswordChange}
            />
            <button onClick={handleLogin}>Login</button>
            {owedAmount !== null ? (
                <p>Owed Amount: {owedAmount}</p>
            ) : (
                <p></p>
            )}
        </div>
    );
};

export default AmountOwed;
