import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";

const FormContainer = styled.div`
  max-width: 400px;
  margin: 0 auto;
  padding: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  display: block;
  width: 100%;
  padding: 10px;
  margin-top: 10px;
  background-color: #4caf50;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #45a049;
  }
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

function Admin() {
  const [userId, setUserId] = useState("");
  const [users, setUsers] = useState([]);
  const [amount, setAmount] = useState("");
  const [owedAmount, setOwedAmount] = useState(null);
  const navigate = useNavigate();

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
  };

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const redirectToWelcomePage = () => {
    navigate(`/welcomePage`);
  };

  const handleView = async (e) => {
    e.preventDefault();
    //like name suggests it prevents default actions
    try {
      const response = await axios.get("http://localhost:8000/admin/viewUsers")
      const result =  response.data

      setUsers(result);

    } catch (error) {
      console.error(error)
    }
  }
  const handleDepositButton = () => {
    const depoField = document.getElementById("depositField");
    depoField.style.display = "block";
  };

  const handleDeposit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8000/admin/depositMoney", {
        amount: amount,
        userId: userId,
      });
      const data = response.data;
      if (response.status === 200) {
        alert("Deposit successful");
      } else {
        alert(data.error || "Failed to deposit money");
      }
    } catch (error) {
      console.error("Error depositing money:", error);
      alert(`Failed to deposit money: ${error.message}`);
    }
  };

  return (
      <div>
      <Button onClick={handleView}>View Users</Button>
      {users.length > 0 && (
        <div>
          <h3>Users:</h3>
          <ul>
            {users.map((user) => (
              <li key={user.customer_id}>{user.customer_id}: {user.name}</li>
            ))}
          </ul>
        </div>
      )}
      <Button onClick={redirectToWelcomePage}>Approve Loans</Button>

      <Button onClick={handleDepositButton}>Deposit Money</Button>
      <FormContainer id="depositField" style={{ display: 'none' }}>
        <Input
          type="text"
          placeholder="UserId"
          value={userId}
          onChange={handleUserIdChange}
        />
        <Input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={handleAmountChange}
        />
        <Button onClick={handleDeposit}>Deposit</Button>
      </FormContainer>
      {owedAmount !== null && <p>Owed Amount: {owedAmount}</p>}
    </div>
  );
}

export default Admin;
